import React, { useState } from 'react';


const CoinToss = ({ setResult, setSpinning }) => {
  const tossCoin = () => {
    setSpinning(true);

    const totalRotations = 5; // Adjust the total number of rotations based on your preference
    const rotationDuration = 1000; // Adjust the rotation duration based on your preference

    // Randomly determine the result after the spinning animation completes
    setTimeout(() => {
      const randomResult = Math.random() < 0.5 ? 'Heads' : 'Tails';
      setResult(randomResult);
      setSpinning(false);
    }, totalRotations * rotationDuration);
  };

  return (
    <div>
      <button onClick={tossCoin} disabled={setSpinning}>
        Toss Coin
      </button>
    </div>
  );
};





const CenteredBox = () => {
  const [result, setResult] = useState(null);

  const boxStyle = {
    width: '800px',
    height: '500px',
    margin: 'auto',
    marginTop: '50px',
    border: '1px solid #ccc',
    borderRadius: '5px',
    textAlign: 'center',
    padding: '20px',
    boxSizing: 'border-box',
    overflow: 'hidden', // Prevent content from overflowing
    overflowY: 'auto', // Add vertical scrollbar if needed
  };


  const circleStyle = {
    width: '200px',
    height: '200px',
    borderRadius: '50%',
    backgroundColor: '#FFD700', // You can change the color as needed
    color: 'black',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    position: 'absolute',
    top: '40%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
  };




  return (
    <div style={boxStyle}>
      <h1>Coin Toss</h1>
      <p>Heads (or) Tails</p>
      <div style={circleStyle}>


      </div>

    </div>
  );
};

export default CenteredBox;
