import React, { useState } from 'react';

const CenteredBox = () => {
  const [result, setResult] = useState(null);

  const coinToss = () => {
    if (Math.random() < 0.5) {
      setResult('heads');
      console.log('heads');
    } else {
      setResult('tails');
      console.log('tails');
    }
  };

  const boxStyle = {
    width: '800px',
    height: '500px',
    margin: 'auto',
    marginTop: '50px',
    border: '1px solid #ccc',
    borderRadius: '5px',
    textAlign: 'center',
    padding: '20px',
    boxSizing: 'border-box',
    overflow: 'hidden',
    overflowY: 'auto',
  };

  const coinBoxStyle = {
    width: '200px',
    height: '200px',
    borderRadius: '50%',
    backgroundColor: '#FFD700',
    color: 'black',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    position: 'relative',
    margin: 'auto',
    marginBottom: '20px',
  };

  const coinStyle = {
    width: '100%',
    height: '100%',
    borderRadius: '50%',
    position: 'absolute',
    top: '0',
    left: '0',
    transform: `translate(-50%, -50%) rotate(${result === 'heads' ? '1800deg' : '0deg'})`,
    transition: 'transform 5s ease-in-out',
  };

  return (
    <div style={boxStyle}>
      <h1>Coin Toss</h1>
      <p>Heads (or) Tails</p>
      <div style={coinBoxStyle}>
        <div id="coin" className={result} key={+new Date()} style={coinStyle}>
          <div className="side-a">
            <h2>TAIL</h2>
          </div>
          <div className="side-b">
            <h2>HEAD</h2>
          </div>
        </div>
      </div>
      <button id="btn" onClick={coinToss}>
        Coin Toss
      </button>
    </div>
  );
};

export default CenteredBox;
