import React, { useState } from 'react';


const CoinToss = () => {
  const [result, setResult] = useState(null);

  const tossCoin = (side) => {
    const randomResult = Math.random() < 0.5 ? 'Heads' : 'Tails';
    setResult(randomResult);

    return(
      <div>
        <button onCLick={tossCoin}>Toss Coin</button>
      </div>
    )


  };
}


const CenteredBox = () => {
  const [result, setResult] = useState(null);

  const boxStyle = {
    width: '800px',
    height: '500px',
    margin: 'auto',
    marginTop: '50px',
    border: '1px solid #ccc',
    borderRadius: '5px',
    textAlign: 'center',
    padding: '20px',
    boxSizing: 'border-box',
    overflow: 'hidden', // Prevent content from overflowing
    overflowY: 'auto', // Add vertical scrollbar if needed
  };


  const circleStyle = {
    width: '200px',
    height: '200px',
    borderRadius: '50%',
    backgroundColor: '#FFD700', // You can change the color as needed
    color: 'black',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    position: 'absolute',
    top: '40%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
  };





};

export default CenteredBox;
