import React from 'react';

const CenteredBox = () => {
  const boxStyle = {
    width: '800px',
    height: '500px',
    margin: 'auto',
    marginTop: '50px',
    border: '1px solid #ccc',
    borderRadius: '5px',
    textAlign: 'center',
    padding: '20px',
    boxSizing: 'border-box',
    overflow: 'hidden', // Prevent content from overflowing
    overflowY: 'auto', // Add vertical scrollbar if needed
  };


  const circleStyle = {
    width: '200px',
    height: '200px',
    borderRadius: '50%',
    backgroundColor: '#3498db', // You can change the color as needed
    color: '#fff',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    position: 'absolute',
    top: '10%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
  };



  return (
    <div style={boxStyle}>
      <h1>Coin Toss</h1>
      <p>Heads (or) Tails</p>
      <div style={circleStyle}>
        <p>Heads</p>
        {/* or */}
        {/* <p>Tails</p> */}
      </div>
      <p>

      </p>
    </div>
  );
};

export default CenteredBox;
