import React, { useState } from 'react';

const CoinToss = () => {
  const [result, setResult] = useState(null);

  const tossCoin = (side) => {
    const randomResult = Math.random() < 0.5 ? 'Heads' : 'Tails';
    setResult(randomResult);
  };

  const circleStyle = {
    width: '200px',
    height: '200px',
    borderRadius: '50%',
    backgroundColor: '#FFD700', // You can change the color as needed
    color: 'black',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    position: 'absolute',
    top: '60%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
  };

  const buttonStyle = {
    margin: '10px',
    padding: '10px',
    fontSize: '16px',
    cursor: 'pointer',
    top: '10%',
    left: '50%',
  };

  return (
    <div>
      <div style={circleStyle}>
        {result && <p>{result}</p>}
      </div>
      <div>
        <button style={buttonStyle} onClick={() => tossCoin('Heads')}>
          Heads
        </button>
        <button style={buttonStyle} onClick={() => tossCoin('Tails')}>
          Tails
        </button>
      </div>
      <button style={buttonStyle} onClick={() => tossCoin()}>
        Let's Roll
      </button>
    </div>
  );
};

const CenteredBox = () => {
  const boxStyle = {
    width: '800px',
    height: '500px',
    margin: 'auto',
    marginTop: '50px',
    border: '1px solid #ccc',
    borderRadius: '5px',
    textAlign: 'center',
    padding: '20px',
    boxSizing: 'border-box',
    overflow: 'hidden',
    position: 'relative',
  };

  return (
    <div style={boxStyle}>
      <h1>Coin Toss</h1>
      <p>Heads (or) Tails</p>
      <CoinToss />
      <p>

      </p>
    </div>
  );
};

export default CenteredBox;
