import React, { useState } from 'react';

const CoinToss = ({ setResult }) => {
  const tossCoin = () => {
    const randomResult = Math.random() < 0.5 ? 'Heads' : 'Tails';
    setResult(randomResult);
  };

  return (
    <div>
      <button onClick={tossCoin}>Toss Coin</button>
    </div>
  );
};

const CenteredBox = () => {
  const [result, setResult] = useState(null);

  const boxStyle = {
    width: '800px',
    height: '500px',
    margin: 'auto',
    marginTop: '50px',
    border: '1px solid #ccc',
    borderRadius: '5px',
    textAlign: 'center',
    padding: '20px',
    boxSizing: 'border-box',
    overflow: 'hidden',
    overflowY: 'auto',
  };

  const circleStyle = {
    width: '200px',
    height: '200px',
    borderRadius: '50%',
    backgroundColor: '#FFD700',
    color: 'black',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    position: 'absolute',
    top: '40%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
  };

  return (
    <div style={boxStyle}>
      <h1>Coin Toss</h1>
      <p>Heads (or) Tails</p>
      <div style={circleStyle}>
        {result && <p>{result}</p>}
      </div>
      <CoinToss setResult={setResult} />
    </div>
  );
};

export default CenteredBox;
