import React, { useState, useEffect } from 'react';

const CoinToss = ({ setResult, setSpinning }) => {
  const tossCoin = () => {
    setSpinning(true);

    const totalRotations = 5;
    const rotationDuration = 1000;

    // Randomly determine the result after the spinning animation completes
    setTimeout(() => {
      const randomResult = Math.random() < 0.5 ? 'Heads' : 'Tails';
      setResult(randomResult);
      setSpinning(false);
    }, totalRotations * rotationDuration);
  };

  return (
    <div>
      <button onClick={tossCoin} disabled={setSpinning}>
        Toss Coin
      </button>
    </div>
  );
};

const CenteredBox = () => {
  const [result, setResult] = useState(null);
  const [spinning, setSpinning] = useState(false);

  const boxStyle = {
    width: '800px',
    height: '500px',
    margin: 'auto',
    marginTop: '50px',
    border: '1px solid #ccc',
    borderRadius: '5px',
    textAlign: 'center',
    padding: '20px',
    boxSizing: 'border-box',
    overflow: 'hidden',
    overflowY: 'auto',
  };

  const circleStyle = {
    width: '200px',
    height: '200px',
    borderRadius: '50%',
    backgroundColor: '#FFD700',
    color: 'black',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    position: 'absolute',
    top: '40%',
    left: '50%',
    transform: `translate(-50%, -50%) rotate(${spinning ? '1800deg' : '0deg'})`, // Rotate when spinning is true
    transition: 'transform 5s ease-in-out',
    animation: 'spin 5s linear infinite',  // CSS animation for spinning
  };

  useEffect(() => {
    if (!spinning) {
      setTimeout(() => {
        setResult(null);
      }, 1000);
    }
  }, [spinning]);

  return (
    <div style={boxStyle}>
      <h1>Coin Toss</h1>
      <p>Heads (or) Tails</p>
      <div style={circleStyle}></div>
      <CoinToss setResult={setResult} setSpinning={setSpinning} />
    </div>
  );
};

export default CenteredBox;
