import React, { useState } from 'react';
import './styles.css';  // Import your CSS file

const CoinToss = ({ setResult, setSpinning }) => {
  const tossCoin = () => {
    setSpinning(true);

    setTimeout(() => {
      const randomResult = Math.random() < 0.5 ? 'Heads' : 'Tails';
      setResult(randomResult);
      setSpinning(false);
    }, 1000);  // Adjust the duration based on your preference
  };

  return (
    <div>
      <button onClick={tossCoin} disabled={setSpinning}>
        Toss Coin
      </button>
    </div>
  );
};

const CenteredBox = () => {
  const [result, setResult] = useState(null);
  const [spinning, setSpinning] = useState(false);

  const boxStyle = {
    // ... (unchanged)
  };

  const circleStyle = {
    // ... (unchanged)
    transition: 'transform 1s ease-in-out',  // CSS transition for spinning animation
    transform: spinning ? 'rotate(360deg)' : 'rotate(0deg)',  // Rotate when spinning is true
  };

  const imageStyle = {
    maxWidth: '100%',
    maxHeight: '100%',
  };

  return (
    <div style={boxStyle}>
      <h1>Coin Toss</h1>
      <p>Heads (or) Tails</p>
      <div style={circleStyle}>
        {result === 'Heads' && <img src={headsImage} alt="Heads" style={imageStyle} />}
        {result === 'Tails' && <img src={tailsImage} alt="Tails" style={imageStyle} />}
      </div>
      <CoinToss setResult={setResult} setSpinning={setSpinning} />
    </div>
  );
};

export default CenteredBox;
