import React, { useState } from 'react';

const CenteredBox = () => {
  const [result, setResult] = useState(null);

  const coinToss = () => {
    if (Math.random() < 0.5) {
      setResult('heads');
      console.log('heads');
    } else {
      setResult('tails');
      console.log('tails');
    }
  };

  const boxStyle = {
    width: '800px',
    height: '500px',
    margin: 'auto',
    marginTop: '50px',
    border: '1px solid #ccc',
    borderRadius: '5px',
    textAlign: 'center',
    padding: '20px',
    boxSizing: 'border-box',
    overflow: 'hidden',
    overflowY: 'auto',
  };

  return (
    <div style={boxStyle}>
      <h1>Coin Toss</h1>
      <p>Heads (or) Tails</p>
      <div id="coin" className={result} key={+new Date()}>
        <div className="side-a">
          <h2>TAIL</h2>
        </div>
        <div className="side-b">
          <h2>HEAD</h2>
        </div>
      </div>
      <button id="btn" onClick={coinToss}>
        Coin Toss
      </button>
    </div>
  );
};

export default CenteredBox;
