import React, { useState } from 'react';





const CenteredBox = () => {
  const [result, setResult] = useState(null);

  const boxStyle = {
    width: '800px',
    height: '500px',
    margin: 'auto',
    marginTop: '50px',
    border: '1px solid #ccc',
    borderRadius: '5px',
    textAlign: 'center',
    padding: '20px',
    boxSizing: 'border-box',
    overflow: 'hidden', // Prevent content from overflowing
    overflowY: 'auto', // Add vertical scrollbar if needed
  };


  class App extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        result: "",
        nader: "nader"
      };
      this.coinToss = this.coinToss.bind(this);
    }
    coinToss() {
      this.setState({ nader: "" }, () => {
        if (Math.random() < 0.5) {
          this.setState({ result: "heads" });
          console.log("heads");
        } else {
          this.setState({ result: "tails" });
          console.log("tails");
        }
      });
    }

    render() {
      return (
        <div className="App" style="boxStyle">
          <div id="coin" className={this.state.result} key={+new Date()}>
            <div class="side-a">
              <h2>TAIL</h2>
            </div>
            <div className="side-b">
              <h2>HEAD</h2>
            </div>
          </div>
          <h1>Flip a coin</h1>
          <button id="btn" onClick={this.coinToss}>
            Coin Toss
          </button>
        </div>
      );
    }
  }






  return (
    <div style={boxStyle}>
      <h1>Coin Toss</h1>
      <p>Heads (or) Tails</p>


    </div>
  );
};

export default CenteredBox;
